import React, { useState } from "react"

import { ReactComponent as Html } from "./svgs/brands/html5.svg";
import { ReactComponent as Css } from "./svgs/brands/css3-alt.svg";
import { ReactComponent as JavaScript } from "./svgs/brands/js.svg";
import { ReactComponent as Reactjs } from "./svgs/brands/react.svg";
import { ReactComponent as Bootstrap } from "./svgs/brands/bootstrap.svg";

import { ReactComponent as Ejs } from "./svgs/brands/ejs.svg";
import { ReactComponent as Jquery } from "./svgs/brands/jquery.svg";

import { ReactComponent as Less } from "./svgs/brands/less.svg";
import { ReactComponent as Git } from "./svgs/brands/git-alt.svg";
import { ReactComponent as Github } from "./svgs/brands/github.svg";

import { ReactComponent as Seo } from "./svgs/brands/seo.svg";
import { ReactComponent as Security } from "./svgs/brands/security.svg";

import { ReactComponent as Nodejs } from "./svgs/brands/node.svg";

import { ReactComponent as Mongodb } from "./svgs/brands/mongodb.svg";
import { ReactComponent as Express } from "./svgs/brands/express.svg";
import { ReactComponent as Sql } from "./svgs/brands/sql.svg";
import { ReactComponent as HyperTerminal } from "./svgs/brands/hyperterminal.svg";
import { ReactComponent as Vscode } from "./svgs/brands/vscode.svg";

function Skills(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const [fadeOutEffect, setfadeOutEffect] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });

  const [normalH3, clickH3] = useState("Skills & Tools");

  const [skillListclassName, setSkillListclassName] = useState("skill-list");

  const [faviconClassName, setfaviconClassName] = useState("logo")

  function handleClick(){
    if (!isClicked) {
      setNavIconClicked("navbarClicked");
      clickH3("");
      setIsClicked(true)
      setSkillListclassName("hidden");
      setfaviconClassName("hidden")
    }else{
      setNavIconClicked("navbarClicked2");
      clickH3("Skills & Tools");
      setIsClicked(false);
      setSkillListclassName("skill-list");
      setfaviconClassName("logo")
    }

    if (!isMenuClicked) {
      setFadeOutMenu({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setIsMenuClicked(true);
    }
    else{
      setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setIsMenuClicked(false);
    }
  };

    return (

      <>

      <section id="skills" className="sections skills-section color-443829">
            <div className="overlay2"></div>
            <h3 style={fadeOutEffect}>{normalH3}</h3>

            <div style={fadeOutEffect} className={skillListclassName}>

            <Html className="skill1 circle" />
            <p className="skill1-text">HTML</p>

            <Css className="skill2 circle" />
            <p className="skill2-text">CSS</p>
            
            <JavaScript className="skill3 circle" />
            <p className="skill3-text">JavaScript</p>
            
            <Reactjs className="skill4 circle" />
            <p className="skill4-text">React</p>
            
            <Bootstrap className="skill5 circle" />
            <p className="skill5-text">Bootstrap</p>
            
            <Ejs className="skill6 circle" />
            <p className="skill6-text">EJS</p>
            
            <Jquery className="skill7 circle" />
            <p className="skill7-text">jQuery</p>
            
            <Less className="skill8 circle" />
            <p className="skill8-text">Less</p>
            
            <Git className="skill9 circle" />
            <p className="skill9-text">Git</p>
            
            <Github className="skill10 circle" />
            <p className="skill10-text">Github</p>
            
            <Seo className="skill11 circle" />
            <p className="skill11-text">SEO</p>
            
            <Security className="skill12 circle" />
            <p className="skill12-text">Security</p>
            
            <Nodejs className="skill13 circle" />
            <p className="skill13-text">Node.js</p>
            
            <Mongodb className="skill14 circle" />
            <p className="skill14-text">MongoDB</p>
            
            <Express className="skill15 circle" />
            <p className="skill15-text">Express</p>
            
            <Sql className="skill16 circle" />
            <p className="skill16-text">SQL</p>
            
            <HyperTerminal className="skill17 circle" />
            <p className="skill17-text">HyperTerminal</p>
            
            <Vscode className="skill18 circle" />
