import React, { useState } from "react"

function Body(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);


  function handleClick(){
    if (!isClicked) {
      setNavIconClicked("navbarClicked");
      setIsClicked(true)
    }else{
      setNavIconClicked("navbarClicked2");
      setIsClicked(false)
    }

    if (!isMenuClicked) {
      setFadeOutMenu({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setIsMenuClicked(true);
    }else{
      setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setIsMenuClicked(false);
    }
  };

    return (

      <>

      <h1>Hi there, I'm Jeffin</h1>
      <h3>A Full-Stack Web Developer</h3>

  <header>
  <div class="pos-f-t" id="my-navbar-collapse">
      <div class="collapse navbar-collapse" id="navbarToggleExternalContent">
        <div class="menu" style={fadeOutMenu}>
          <ul class="nav navbar-nav">
            <li>
              <a class="nav-link" href="#about-me">Projects</a>
            </li>
            <li>
              <a class="nav-link" href="#skills">Contact</a>
            </li>
            <li>
              <a class="nav-link" href="#contact-me">Resume</a>
            </li>
          </ul>
        </div>
      </div>
      <nav class="navbar navbar-dark">
        <button class="navbar-toggler" 
        type="button" 
        data-toggle="collapse" 
        data-target="#navbarToggleExternalContent"
        aria-controls="navbarToggleExternalContent"
        aria-expanded="false"
        aria-label="Toggle navigation"
        onClick={handleClick}>
          <span class={navIconClicked}></span>
        </button>
      </nav>
    </div>
  </header>

  <div class="about">
    <h5>I'm a firm believer that anything is possible with patience and perseverence, this leaves troubleshooting out of the way.
      Being a second generation expatriate, I've always felt the responsibility to focus time on building a better future and all the expertise I have today is a result of this dream.
      To me, creating websites is something that brings joy because I'm able to fully express creatively, venture into new areas of knowledge everyday and be of help to new people.
      I'm currently located in The Kingdom of Bahrain, I work independently as a freelancer but I'm also available for hiring or collaborations.</h5>
  </div>

</>

)};

export default Body;
