import React, { useState } from "react"

function About(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const [fadeOutTitles, setFadeOutTitles] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });

  const [normalH3, clickH3] = useState("About");
  const [normalPara1, clickPara1] = useState("I'm a firm believer that anything is possible with patience and perseverence.");
  const [normalPara2, clickPara2] = useState("Being a second generation expatriate, I've always felt the responsibility to focus time on building a better future and all the expertise I have today is a result of this dream.");
  const [normalPara3, clickPara3] = useState("To me, creating websites is something that brings joy because I'm able to fully express creatively, venture into new areas of knowledge everyday and be of help to new people.");
  const [normalPara4, clickPara4] = useState("I'm currently located in The Kingdom of Bahrain, and I work independently as a freelancer, however, I'm also available for hiring or collaborations.");

  function handleClick(){
    if (!isClicked) {
      setNavIconClicked("navbarClicked");
      clickH3("");
      clickPara1("");
      clickPara2("");
      clickPara3("");
      clickPara4("");
      setIsClicked(true)
    }else{
      setNavIconClicked("navbarClicked2");
      clickH3("About");
      clickPara1("I'm a firm believer that anything is possible with patience and perseverence.");
      clickPara2("Being a second generation expatriate, I've always felt the responsibility to focus time on building a better future and all the expertise I have today is a result of this dream.");
      clickPara3("To me, creating websites is something that brings joy because I'm able to fully express creatively, venture into new areas of knowledge everyday and be of help to new people.");
      clickPara4("I'm currently located in The Kingdom of Bahrain, and I work independently as a freelancer, however, I'm also available for hiring or collaborations.");  
      setIsClicked(false)
    }

    if (!isMenuClicked) {
      setFadeOutMenu({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setFadeOutTitles({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setIsMenuClicked(true);
    }
    else{
      setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setFadeOutTitles({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setIsMenuClicked(false);
    }
  };

    return (

      <>

      <section id="about" class="sections color-655A4E">
            <div class="overlay2"></div>
            <h3 style={fadeOutTitles}>{normalH3}</h3>
            <p style={fadeOutTitles} class="paragraph-1">
            {normalPara1} <br /> <br />
            {normalPara2} <br /> <br />
            {normalPara3} <br /> <br />
            {normalPara4}
            </p>

      </section> 

  <header>
  <div class="pos-f-t" id="my-navbar-collapse">
      <div class="collapse navbar-collapse" id="navbarToggleExternalContent">
        <div class="menu" style={fadeOutMenu}>
          <ul class="nav navbar-nav">
            <li>
              <a class="nav-link nav-links" href="/about">About</a>
            </li>
            <li>
              <a class="nav-link nav-links" href="/skills">Skills & Tools</a>
            </li>
            <li>
              <a class="nav-link nav-links" href="/projects">Projects</a>
            </li>
            <li>
              <a class="nav-link nav-links" href="/contact">Contact</a>
            </li>
            <li>
              <a class="nav-link nav-links" href="Jeffin_Jaison_Resume.pdf">
              <svg class="download-button" xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 24 24"><path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/></svg>
              Resume</a>
            </li>
          </ul>
        </div>
      </div>
      <nav class="navbar navbar-dark">
        <button class="navbar-toggler" 
        type="button" 
        data-toggle="collapse" 
        data-target="#navbarToggleExternalContent"
        aria-controls="navbarToggleExternalContent"
        aria-expanded="false"
        aria-label="Toggle navigation"
        onClick={handleClick}>
          <span class={navIconClicked}></span>
        </button>
      </nav>
    </div>
  </header>

</>

)};

export default About;
