import React, { useState } from "react"

function Body(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const checkTrue = !checkTrue;

  function handleClick(){

    if (checkTrue === true) {
      setIsMenuClicked(
        prevValue=>{
          return {...prevValue, isMenuClicked}
        }
      );
  
      isMenuClicked ? setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "1s"
      }) : null;
  
      setIsClicked(
        prevValue=>{
          return {...prevValue, isClicked}
        }
      );
        
      !isClicked ? setNavIconClicked("navbarClicked") : setNavIconClicked("navbarClicked2"); 
    }

  };
  
    return (

<header>
  <h1>Montana</h1> 
<div class="pos-f-t" id="my-navbar-collapse">
      <div class="collapse navbar-collapse" id="navbarToggleExternalContent">
        <div class="menu" style={fadeOutMenu}>
          <ul class="nav navbar-nav">
            <li>
              <a class="nav-link" href="#about-me">About</a>
            </li>
            <li>
              <a class="nav-link" href="#skills">Skills</a>
            </li>
            <li>
              <a class="nav-link" href="#contact-me">Contact</a>
            </li>
            <li>
              <a class="nav-link" href="Jeffin_Jaison_CV.pdf">CV</a>
            </li>
    
          </ul>
        </div>
      </div>
      <nav class="navbar navbar-dark">
        <button class="navbar-toggler" 
        type="button" 
        data-toggle="collapse" 
        data-target="#navbarToggleExternalContent"
        aria-controls="navbarToggleExternalContent"
        aria-expanded="false"
        aria-label="Toggle navigation"
        onClick={handleClick}>
          <span class={navIconClicked}></span>
        </button>
      </nav>
    </div>
</header>

)};

export default Body;
