import React, { useState } from "react";
import Simonsgame from "./components/assets/images/simons-game.png";

const [project1ClassName, setProject1ClassName] = useState("slide-item");

function project1MouseOver(){
    setProject1ClassName("slide-item add-opacity")
  }

function project1MouseLeave(){
    setProject1ClassName("slide-item remove-opacity")
  }

const projects = [
    {
        id: 1,
        ProjectMOUSEENTER: project1MouseOver,
        ProjectMOUSELEAVE: project1MouseLeave,
        projectCLASS: project1ClassName,
        projectIMG: Simonsgame,
        projectALT: "slide 1"
    }];

export default projects;
