import React, { useState } from "react";
import projects from "../projects";
import ProjectSlides from "./ProjectSlides";

// import Blogwebsite from "./assets/images/blog-website.png";
// import Dicegame from "./assets/images/dice-game.png";
// import Drumkit from "./assets/images/drum-kit.png";
// import Emojipedia from "./assets/images/emojipedia.png";
// import Keeperapp from "./assets/images/keeper-app.png";
// import Newslettersignup from "./assets/images/newsletter-signup.png";
// import Personalsite from "./assets/images/personal-site.png";
// import Secretswebsite from "./assets/images/secrets-website.png";
import Simonsgame from "./assets/images/simons-game.png";
// import Spacetourism from "./assets/images/space-tourism.png";
// import Spreadoperator from "./assets/images/spread-operator.png";
// import Timeapp from "./assets/images/time-app.png";
// import Tindog from "./assets/images/tindog.png";
// import Todolistv1 from "./assets/images/to-do-list-v1.png";
// import Todolistv2 from "./assets/images/to-do-list-v2.png";

import { ReactComponent as Html } from "./assets/svgs/brands/html5.svg";
import { ReactComponent as Css } from "./assets/svgs/brands/css3-alt.svg";
import { ReactComponent as JavaScript } from "./assets/svgs/brands/js.svg";
import { ReactComponent as Reactjs } from "./assets/svgs/brands/react.svg";
import { ReactComponent as Bootstrap } from "./assets/svgs/brands/bootstrap.svg";
import { ReactComponent as Ejs } from "./assets/svgs/brands/ejs.svg";
import { ReactComponent as Jquery } from "./assets/svgs/brands/jquery.svg";
import { ReactComponent as Less } from "./assets/svgs/brands/less.svg";
import { ReactComponent as Git } from "./assets/svgs/brands/git-alt.svg";
import { ReactComponent as Github } from "./assets/svgs/brands/github.svg";
import { ReactComponent as Seo } from "./assets/svgs/brands/seo.svg";
import { ReactComponent as Security } from "./assets/svgs/brands/security.svg";
import { ReactComponent as Nodejs } from "./assets/svgs/brands/node.svg";
import { ReactComponent as Mongodb } from "./assets/svgs/brands/mongodb.svg";
import { ReactComponent as Express } from "./assets/svgs/brands/express.svg";
import { ReactComponent as Sql } from "./assets/svgs/brands/sql.svg";
import { ReactComponent as HyperTerminal } from "./assets/svgs/brands/hyperterminal.svg";
import { ReactComponent as Vscode } from "./assets/svgs/brands/vscode.svg";

function Home(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const [fadeOutEffect, setfadeOutEffect] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });

  const [normalH1, clickH1] = useState("Hi, I'm Jeffin Jaison");
  const [normalH2, clickH2] = useState("A Full-Stack Web Developer");

  const [normalAboutH3, clickAboutH3] = useState("About");
  const [normalPara1, clickPara1] = useState("I'm a firm believer that anything is possible with persistence, and that opportunities presents itself with each stride.");
  const [normalPara2, clickPara2] = useState("Being a second generation expatriate living in the Kingdom of Bahrain, I found that creating websites brings me joy as I'm able to fully express creatively, venture into new areas of knowledge everyday and solve problems that hold businesses back from gaining reach.");
  const [normalPara3, clickPara3] = useState("I currently work independently as a freelancer, however, I'm also available for hiring or collaborations.");

  const [normalSkillsH3, clickSkillsH3] = useState("Skills & Tools");
  const [skillListclassName, setSkillListclassName] = useState("skill-list");

  const [normalProjectsH3, clickProjectsH3] = useState("Projects");
  const [project1ClassName, setProject1ClassName] = useState("slide-item");
  // const [project2ClassName, setProject2ClassName] = useState("slide-item");
  // const [project3ClassName, setProject3ClassName] = useState("slide-item");
  // const [project4ClassName, setProject4ClassName] = useState("slide-item");
  // const [project5ClassName, setProject5ClassName] = useState("slide-item");
  // const [project6ClassName, setProject6ClassName] = useState("slide-item");
  // const [project7ClassName, setProject7ClassName] = useState("slide-item");
  // const [project8ClassName, setProject8ClassName] = useState("slide-item");
  // const [project9ClassName, setProject9ClassName] = useState("slide-item");
  // const [project10ClassName, setProject10ClassName] = useState("slide-item");
  // const [project11ClassName, setProject11ClassName] = useState("slide-item");
  // const [project12ClassName, setProject12ClassName] = useState("slide-item");
  // const [project13ClassName, setProject13ClassName] = useState("slide-item");
  // const [project14ClassName, setProject14ClassName] = useState("slide-item");
  // const [project15ClassName, setProject15ClassName] = useState("slide-item");

  const [faviconClassName, setfaviconClassName] = useState("logo");

  const year = new Date().getFullYear();

  function handleClick(){
    if (!isClicked) {
      setNavIconClicked("navbarClicked");
      clickH1("");
      clickH2("");
      clickAboutH3("");
      clickPara1("");
      clickPara2("");
      clickPara3("");
      clickSkillsH3("");
      setSkillListclassName("hidden");
      clickProjectsH3("");
      setIsClicked(true);
      setfaviconClassName("hidden");
    }else{
      setNavIconClicked("navbarClicked2");
      clickH1("Hi, I'm Jeffin Jaison");
      clickH2("A Full-Stack Web Developer");
      clickAboutH3("About");
      clickPara1("I'm a firm believer that anything is possible with persistence, and that opportunities presents itself with each stride.");
      clickPara2("Being a second generation expatriate living in the Kingdom of Bahrain, I found that creating websites brings me joy as I'm able to fully express creatively, venture into new areas of knowledge everyday and solve problems that hold businesses back from gaining reach.");
      clickPara3("I currently work independently as a freelancer, however, I'm also available for hiring or collaborations.");
      clickSkillsH3("Skills & Tools");
      setSkillListclassName("skill-list");
      clickProjectsH3("Projects");
      setIsClicked(false);
      setfaviconClassName("logo");
    }

    if (!isMenuClicked) {
      setFadeOutMenu({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setIsMenuClicked(true);
    }
    else{
      setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setIsMenuClicked(false);
    }
  };

  function project1MouseOver(){
    setProject1ClassName("slide-item add-opacity")
  }

  // function project2MouseOver(){
  //   setProject2ClassName("slide-item add-opacity")
  // }

  // function project3MouseOver(){
  //   setProject3ClassName("slide-item add-opacity")
  // }

  // function project4MouseOver(){
  //   setProject4ClassName("slide-item add-opacity")
  // }

  // function project5MouseOver(){
  //   setProject5ClassName("slide-item add-opacity")
  // }

  // function project6MouseOver(){
  //   setProject6ClassName("slide-item add-opacity")
  // }

  // function project7MouseOver(){
  //   setProject7ClassName("slide-item add-opacity")
  // }

  // function project8MouseOver(){
  //   setProject8ClassName("slide-item add-opacity")
  // }

  // function project9MouseOver(){
  //   setProject9ClassName("slide-item add-opacity")
  // }

  // function project10MouseOver(){
  //   setProject10ClassName("slide-item add-opacity")
  // }

  // function project11MouseOver(){
  //   setProject11ClassName("slide-item add-opacity")
  // }

  // function project12MouseOver(){
  //   setProject12ClassName("slide-item add-opacity")
  // }

  // function project13MouseOver(){
  //   setProject13ClassName("slide-item add-opacity")
  // }

  // function project14MouseOver(){
  //   setProject14ClassName("slide-item add-opacity")
  // }

  // function project15MouseOver(){
  //   setProject15ClassName("slide-item add-opacity")
  // }

  function project1MouseLeave(){
    setProject1ClassName("slide-item remove-opacity")
  }

  // function project2MouseLeave(){
  //   setProject2ClassName("slide-item remove-opacity")
