import React, { useState } from "react";
import projects from "../projects";
import ProjectSlides from "./ProjectSlides";

import { ReactComponent as Html } from "./assets/svgs/brands/html5.svg";
import { ReactComponent as Css } from "./assets/svgs/brands/css3-alt.svg";
import { ReactComponent as JavaScript } from "./assets/svgs/brands/js.svg";
import { ReactComponent as Reactjs } from "./assets/svgs/brands/react.svg";
import { ReactComponent as Bootstrap } from "./assets/svgs/brands/bootstrap.svg";
import { ReactComponent as Ejs } from "./assets/svgs/brands/ejs.svg";
import { ReactComponent as Jquery } from "./assets/svgs/brands/jquery.svg";
import { ReactComponent as Less } from "./assets/svgs/brands/less.svg";
import { ReactComponent as Git } from "./assets/svgs/brands/git-alt.svg";
import { ReactComponent as Github } from "./assets/svgs/brands/github.svg";
import { ReactComponent as Seo } from "./assets/svgs/brands/seo.svg";
import { ReactComponent as Security } from "./assets/svgs/brands/security.svg";
import { ReactComponent as Nodejs } from "./assets/svgs/brands/node.svg";
import { ReactComponent as Mongodb } from "./assets/svgs/brands/mongodb.svg";
import { ReactComponent as Express } from "./assets/svgs/brands/express.svg";
import { ReactComponent as Sql } from "./assets/svgs/brands/sql.svg";
import { ReactComponent as HyperTerminal } from "./assets/svgs/brands/hyperterminal.svg";
import { ReactComponent as Vscode } from "./assets/svgs/brands/vscode.svg";

function Home(){

  const [navIconClicked, setNavIconClicked] = useState("navbar-toggler-icon");
  const [isClicked, setIsClicked] = useState(false);

  const [fadeOutMenu, setFadeOutMenu] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });
  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const [fadeOutEffect, setfadeOutEffect] = useState({
    "-webkit-animation-name": "",
    "animation-name": "",
    "animation-duration": ""
  });

  const [navbarCollapseClassName, setNavbarCollapseClassName] = useState("collapse navbar-collapse");

  const [normalH1, clickH1] = useState("Hi, I'm Jeffin Jaison");
  const [normalH2, clickH2] = useState("A Full-Stack Web Developer");

  const [normalAboutH3, clickAboutH3] = useState("About Me");
  const [normalPara1, clickPara1] = useState("I'm a firm believer that anything is possible with persistence, and that opportunities presents itself with each stride.");
  const [normalPara2, clickPara2] = useState("Being a second generation expatriate living in the Kingdom of Bahrain, I found that creating websites brings me joy as I'm able to fully express creatively, venture into new areas of knowledge everyday and solve problems that hold businesses back from gaining reach.");
  const [normalPara3, clickPara3] = useState("I currently work independently as a freelancer, however, I'm also available for hiring or collaborations.");

  const [normalSkillsH3, clickSkillsH3] = useState("Skills & Tools");
  const [skillListclassName, setSkillListclassName] = useState("skill-list");

  const [normalProjectsH3, clickProjectsH3] = useState("Projects");
  const [projectsTickerClassName, setProjectsTickerClassName] = useState("flex img-ticker ticker-stop")
  const [projectsReverseTickerClassName, setProjectsReverseTickerClassName] = useState("flex reverse-img-ticker ticker-stop")

  const [normalContactH3, setContactH3] = useState("Contact Me");
  const [contactFormClassName, setContactFormClassName] = useState("row align-items-stretch mb-5");
  const [contactButtonClassName, setContactButtonClassName] = useState("btn btn-xl text-center");
  const [contactCopyrightClassName, setContactCopyrightClassName] = useState("");

  const [faviconClassName, setfaviconClassName] = useState("logo");

  const year = new Date().getFullYear();

  function handleSectionClick(){
    setNavbarCollapseClassName("collapse navbar-collapse");
  }

  function handleClick(){
    if (!isClicked) {
      setNavIconClicked("navbarClicked");
      clickH1("");
      clickH2("");
      clickAboutH3("");
      clickPara1("");
      clickPara2("");
      clickPara3("");
      clickSkillsH3("");
      setSkillListclassName("hidden");
      clickProjectsH3("");
      setProjectsTickerClassName("hidden");
      setProjectsReverseTickerClassName("hidden");
      setContactH3("");
      setContactFormClassName("hidden");
      setContactButtonClassName("hidden");
      setContactCopyrightClassName("hidden");
      setIsClicked(true);
      setfaviconClassName("hidden");
    }else{
      setNavIconClicked("navbarClicked2");
      clickH1("Hi, I'm Jeffin Jaison");
      clickH2("A Full-Stack Web Developer");
      clickAboutH3("About Me");
      clickPara1("I'm a firm believer that anything is possible with persistence, and that opportunities presents itself with each stride.");
      clickPara2("Being a second generation expatriate living in the Kingdom of Bahrain, I found that creating websites brings me joy as I'm able to fully express creatively, venture into new areas of knowledge everyday and solve problems that hold businesses back from gaining reach.");
      clickPara3("I currently work independently as a freelancer, however, I'm also available for hiring or collaborations.");
      clickSkillsH3("Skills & Tools");
      setSkillListclassName("skill-list");
      clickProjectsH3("Projects");
      setProjectsTickerClassName("flex img-ticker ticker-stop");
      setProjectsReverseTickerClassName("flex reverse-img-ticker ticker-stop");
      setContactH3("Contact Me");
      setContactFormClassName("row align-items-stretch mb-5");
      setContactButtonClassName("btn btn-xl text-center");
      setContactCopyrightClassName("");
      setIsClicked(false);
      setfaviconClassName("logo");
    }

    if (!isMenuClicked) {
      setFadeOutMenu({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setIsMenuClicked(true);
    }
    else{
      setFadeOutMenu({
        "-webkit-animation-name": "fadeOutRight",
        "animation-name": "fadeOutRight",
        "animation-duration": "0.5s"
      })
      setfadeOutEffect({
        "-webkit-animation-name": "fadeInRight",
        "animation-name": "fadeInRight",
        "animation-duration": "1s"
      })
      setIsMenuClicked(false);
    }
  };

  function createSlide(slide) {
    return (
      <ProjectSlides
        key={slide.id}
        link={slide.projectHREF}
        classname={slide.projectCLASS}
        img={slide.projectIMG}
        alt={slide.projectALT}
      />
    );
  }

    return (

      <>

  <section id="home" className="showcase">

  <video className="myVideo" src="assets/videos/sunshine.mp4" playsInline autoPlay muted loop type="video/mp4"></video>
  <div className="overlay"></div>

      <h1 style={fadeOutEffect}>{normalH1}</h1>
      <h2 className="h2-title" style={fadeOutEffect}>{normalH2}</h2>

      <header>

      <a href="/">
      <img className={faviconClassName} src="assets/logo/favicon.ico" />
      </a>

        <div className="pos-f-t" id="my-navbar-collapse">
            <div className="navbar-collapse collapse" id="navbarToggleExternalContent">
              <div className="menu">

              <ul className="nav navbar-nav">
                <li>
              <a onClick={handleSectionClick} style={fadeOutMenu} className="nav-link nav-links" href="#home">Home</a>
            </li>
                  <li>
                    <a onClick={handleSectionClick} style={fadeOutMenu} className="nav-link nav-links" href="#about">About</a>
                  </li>
                  <li>
                    <a onClick={handleSectionClick} style={fadeOutMenu} className="nav-link nav-links" href="#skills">Skills & Tools</a>
                  </li>
                  <li>
                    <a onClick={handleSectionClick} style={fadeOutMenu} className="nav-link nav-links" href="#projects">Projects</a>
                  </li>
                  <li>
                    <a onClick={handleSectionClick} style={fadeOutMenu} className="nav-link nav-links" href="#contact">Contact</a>
                  </li>
                  <li>
                  <div style={fadeOutMenu}>
                    <a className="nav-link nav-links" href="Jeffin_Jaison_Resume.pdf"><svg className="download-button" xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 24 24"><path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/></svg>Resume</a>
                    </div>
                  </li>
                </ul>
              </div>
            </div>

            <nav className="navbar navbar-dark">
              <button className="navbar-toggler" 
              type="button" 
              data-toggle="collapse" 
              data-target="#navbarToggleExternalContent"
              aria-controls="navbarToggleExternalContent"
              aria-expanded="false"
              aria-label="Toggle navigation"
              onClick={handleClick}>
                <span className={navIconClicked}></span>
              </button>
            </nav>
          </div>
        </header>

        </section>

        <section id="about" className="about-section color-1c323b">
            <div className="overlay2"></div>
            <div className="about-box-container">
            <div className="about-box-outline">
            <div className="about-title-bg">
            <h3 style={fadeOutEffect}>{normalAboutH3}</h3>
            </div>
            <p style={fadeOutEffect} className="paragraph-1">
            {normalPara1} <br /> <br />
            {normalPara2} <br /> <br />
            {normalPara3}
            </p>
            </div>
            </div>

      </section>

        <section className="skills-section color-1c323b">
            <div className="overlay2"></div>

            <div className="skills-box-container">
            <div className="skills-box-outline">
            <div className="skills-title-bg">

            <h3 id="skills" style={fadeOutEffect}>{normalSkillsH3}</h3>

            <div style={fadeOutEffect} className={skillListclassName}>

            <Html className="skill1 circle" />
            <p className="skill1-text">HTML</p>

            <Css className="skill2 circle" />
            <p className="skill2-text">CSS</p>
            
            <JavaScript className="skill3 circle" />
            <p className="skill3-text">JavaScript</p>
            
            <Reactjs className="skill4 circle" />
            <p className="skill4-text">React</p>
            
